// verify the given message and signature
const char* message="Turn off the light";
const char* received_signature= "eac21db07a16018a52933068b0849e4de34ff7eb745f35c326308cc67680dc2c0149fdea7641cb9f";

const char* received_public_key= "";


const unsigned char* HexStr2UnignedChar(const char* hex, size_t* res_len)
{
    int len = strlen(hex);
    unsigned char* res = (unsigned char*)malloc(len/2);
    
    for(int i = 0; i < len; i+=2)
    {
        char tmp[3] = {hex[i], hex[i+1], '\0'};
        res[i/2] = (unsigned char)strtol(tmp, NULL, 16);
    }
    *res_len = len/2;
    return res;
}
void print_byte_stream(const unsigned char *arr, int len) {
    for (int i = 0; i < len; i++) {
        printf("%x", arr[i]);
    }
}

int verify_sign(const char* received_sigiture, const char* received_public_key, const char* message){
    const int ovn = 40;  // oil + vinegar variables
    const int v1 = 8;
    const int nv1 = ovn - v1;
    const int ovn1 = ovn+1;
    const int mlen = nv1;
    const int smlen = ovn;
    // secert key and public key
    const int pklen = 27552;

    //calculate the md5 hash of the message
    unsigned char md5_hash[16];
    cal_md5(message, md5_hash);
    unsigned char double_hash[33];
    // convert recieved signature to unsigned char
    size_t sig_len;
    const unsigned char* signature = HexStr2UnignedChar(received_sigiture, &sig_len);
    // show the signature
    printf("\nsignature is: ");
    print_byte_stream(signature, sig_len);
    printf("\n");
    
    // convert recieved public key to unsigned char
    size_t pk_len;
    const unsigned char* public_key = HexStr2UnignedChar(received_public_key, &pk_len);

    // show the public_key
    printf("public key is: ");
    print_byte_stream(public_key, pk_len);
    printf("\n");
    for (int i = 0; i < 16; i++){
        double_hash[i] = md5_hash[i];
        double_hash[i+16] = md5_hash[i];
    }
    printf("double hash is: ");
    print_byte_stream(double_hash, 32);
    printf("\n");

    int eof;
    eof = shortMessageSigned(double_hash, signature, public_key, ovn1, nv1, ovn);
    if (eof == -100){
        printf("The signature is invalid!\n");
        return -1;
    }
    else{
        printf("The signature is valid!\n");
        return 0;
    }
}

void Rainbow_app(void){
    int res = verify_sign(received_signature, received_public_key, message);
    printf("res is %d\n",res);
}
SYS_RUN(Rainbow_app);